#!/bin/bash

# Make a series of requests to the RTN to make sure previews are working as expected
# DEPENDENCIES: curl, sed, tr, perl

query_and_extract(){
    url=$1
    result=$(curl -s "$url" | sed 's/"/\&quot;/g' | tr '\n' "N" | sed -r 's/.*<meta name=&quot;description&quot; content=&quot;//' | sed -r 's/&quot;.*//g')
    echo -n "$result" > ./result.txt
    echo -n "$result"
}

check(){
    url=$1
    target=$2
    testNum=$3

    input=$(query_and_extract "$url")

    if [[ $input =~ ^$target ]]; then
        echo -e "[SUCCESS $testNum\t]"
        #echo "$random_compressor,$random_string,$result" >> ./successes.log.txt
    fi
    if [[ ! $input =~ ^$target ]]; then
        echo -e "[FAILURE $testNum\t]\n$input"
        echo "$testNum,$input,$target" >> ./failures.log.txt
    fi
}

